mapper.update:
update resume_intention set
delete_flag = '1'
where id = #{id}

mapper.select:
select p.*,d1.name intentionName from resume_intention p left join dict d1 on d1.id=p.intention where p.delete_flag = '0'
and p.resume_id =#{resumeId,jdbcType=VARCHAR}
and p.intention =#{intention,jdbcType=VARCHAR}
and d1.name LIKE CONCAT('%', CONCAT(#{intentionName,jdbcType=VARCHAR}, '%'))
order by p.last_modify_time,p.create_time desc

mapper.insert:
		insert into resume_intention
		(id, resume_id, intention,delete_flag,create_time,create_user_id,last_modify_time,last_modify_user_id)
		values

        (
	        uuid(), #{item.resumeId}, #{item.intention},'0', CURRENT_TIMESTAMP(),#{item.createUserId},CURRENT_TIMESTAMP(), #{item.lastModifyUserId}
        )
